/**
 * Creates a glowing sphere element that can be used inline with text
 * or as the source for particle splitting animation.
 */

interface SphereHeroOptions {
  size?: number;
  color?: string;
  glowColor?: string;
  glowIntensity?: number;
}

export function createSphereHero(options: SphereHeroOptions = {}) {
  const {
    size = 24,
    color = 'var(--sphere-color, #fff)',
    glowColor = 'var(--sphere-glow, rgba(255, 255, 255, 0.6))',
    glowIntensity = 1,
  } = options;

  const sphere = document.createElement('span');
  sphere.className = 'hero-sphere';

  // Set CSS custom properties for dynamic styling
  sphere.style.setProperty('--sphere-size', `${size}px`);
  sphere.style.setProperty('--sphere-color', color);
  sphere.style.setProperty('--sphere-glow', glowColor);
  sphere.style.setProperty('--glow-intensity', String(glowIntensity));

  return sphere;
}

export function getSpherePosition(sphere: HTMLElement): { x: number; y: number } {
  const rect = sphere.getBoundingClientRect();
  return {
    x: rect.left + rect.width / 2 + window.scrollX,
    y: rect.top + rect.height / 2 + window.scrollY,
  };
}

// Create and export default sphere
const sphere = createSphereHero({ size: 28 });
export default sphere;
