import { distance2D, midpoint } from './block-import-library.org?name=type-safe-utils';

const p1 = { x: 0, y: 0 };
const p2 = { x: 3, y: 4 };

const dist = distance2D(p1, p2);
const mid = midpoint(p1, p2);

const container = document.createElement('div');
container.style.cssText = 'padding: 1rem; background: #fff3e0; border-radius: 8px; margin: 1rem 0;';
container.innerHTML = `
  <h4 style="margin-top: 0; color: #f57c00;">TypeScript Utils from Library</h4>
  <p>Point 1: (${p1.x}, ${p1.y})</p>
  <p>Point 2: (${p2.x}, ${p2.y})</p>
  <p>Distance: ${dist.toFixed(2)}</p>
  <p>Midpoint: (${mid.x}, ${mid.y})</p>
`;

export default container;


function render(result, el) {
  // Skip null/undefined
  if (result === null || result === undefined) return;
  // Handle function exports (render function that takes container ID)
  if (typeof result === "function") {
    result(el.id);
  }
  // Handle HTMLElement exports
  else if (result instanceof HTMLElement) {
    if (!el.hasChildNodes()) el.appendChild(result);
  }
  // Handle string exports (could be HTML or plain text)
  else if (typeof result === "string") {
    if (result.trim().startsWith("<")) {
      el.innerHTML = result;
    } else {
      el.textContent = result;
    }
  }
  // Handle numbers and booleans
  else if (typeof result === "number" || typeof result === "boolean") {
    el.textContent = String(result);
  }
  // Handle objects/arrays (display as formatted JSON)
  else if (typeof result === "object") {
    el.innerHTML = "<pre>" + JSON.stringify(result, null, 2) + "</pre>";
  }
}
export { render };