
import renderExcalidraw from '@org-press/block-excalidraw/wrapper';

export default function render(containerId) {
  const container = document.getElementById(containerId);
  if (!container) {
    console.error('[Excalidraw] Container not found:', containerId);
    return;
  }

  container.className = 'excalidraw-wrapper';
  container.style.cssText = 'position: relative; height: 400px;';

  try {
    const data = {
  "type": "excalidraw",
  "version": 2,
  "elements": [
    {
      "type": "rectangle",
      "x": 50,
      "y": 50,
      "width": 150,
      "height": 80,
      "strokeColor": "#1e1e1e",
      "backgroundColor": "#a5d8ff",
      "fillStyle": "solid",
      "strokeWidth": 2,
      "roughness": 0,
      "roundness": { "type": 3 }
    },
    {
      "type": "text",
      "x": 90,
      "y": 75,
      "text": "Frontend",
      "fontSize": 20,
      "strokeColor": "#1e1e1e"
    },
    {
      "type": "arrow",
      "x": 200,
      "y": 90,
      "points": [[0, 0], [100, 0]],
      "strokeColor": "#1e1e1e",
      "strokeWidth": 2,
      "endArrowhead": "arrow"
    },
    {
      "type": "rectangle",
      "x": 300,
      "y": 50,
      "width": 150,
      "height": 80,
      "strokeColor": "#1e1e1e",
      "backgroundColor": "#b2f2bb",
      "fillStyle": "solid",
      "strokeWidth": 2,
      "roughness": 0,
      "roundness": { "type": 3 }
    },
    {
      "type": "text",
      "x": 345,
      "y": 75,
      "text": "Backend",
      "fontSize": 20,
      "strokeColor": "#1e1e1e"
    },
    {
      "type": "arrow",
      "x": 375,
      "y": 130,
      "points": [[0, 0], [0, 50]],
      "strokeColor": "#1e1e1e",
      "strokeWidth": 2,
      "endArrowhead": "arrow"
    },
    {
      "type": "rectangle",
      "x": 300,
      "y": 180,
      "width": 150,
      "height": 80,
      "strokeColor": "#1e1e1e",
      "backgroundColor": "#ffd43b",
      "fillStyle": "solid",
      "strokeWidth": 2,
      "roughness": 0,
      "roundness": { "type": 3 }
    },
    {
      "type": "text",
      "x": 335,
      "y": 205,
      "text": "Database",
      "fontSize": 20,
      "strokeColor": "#1e1e1e"
    }
  ],
  "appState": {
    "viewBackgroundColor": "#ffffff"
  }
}
;
    const mode = 'view';
    const filePath = undefined;
    const blockId = 'architecture-example';

    renderExcalidraw(container, data, mode, filePath, blockId);
  } catch (err) {
    console.error('[Excalidraw] Failed to render diagram:', err);
    container.innerHTML = '<div style="color: red; padding: 1rem; border: 1px solid red;">Error rendering Excalidraw diagram: ' + err.message + '</div>';
  }
}
        